import tg from './tag';
import { Component } from './component';
import core from './components/core';
import geo from './components/geo';
import bs4 from './components/bootstrap';
import vui from './components/vui';

let componentBody;
export function renderDOM(component) {
    if (componentBody) {
        componentBody.remove();
    }
    
    const componentBodyTag = tg.div({
        id: 'component-body',
        style: 'height: 100%; width: 100%;'
    });
    componentBody = document.body.appendChild(componentBodyTag.render());
    
    var body = new component();
    componentBody.appendChild(body.render());
    body.activate();
}

window.onload = function () {
    // todo: should find a way to add this back in 
    renderDOM(Body);
};

onpopstate = function (e) {
    // todo: should find a way to add this back in     
    renderDOM(Body);
};

export const vila = {
    Component,
    geo,
    core,
    bs4,
    vui,
    tg,
};
